import Image from 'next/image';
import { useState } from 'react';

const MyImage = ({ src }) => {
    const [imgSrc, setImgSrc] = useState(src);
    const fallback = "data:image/png;base64,"


    return (
        <Image
            src={imgSrc}
            onError={() => setImgSrc(fallback)}
            alt="image"
        />
    );
};

export default MyImage;
